import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router";
import { getProfileById } from "../../actions/profile";

const ProfileTop = () => {
  const loading = useSelector((state) => state.profile.loading);
  const profile = useSelector((state) => state.profile.profile);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getProfileById(id));
  }, [dispatch, id]);

  return (
    <div class="profile-top bg-primary p-2">
      <img class="round-img my-1" src={profile.avatar} alt="" />
      <h1 class="large">{profile.user.name}</h1>
      <p class="lead">
        {profile.status} {profile.company && <span> at {profile.company} </span>}
      </p>
      <p>{profile.location}</p>
      <div class="icons my-1">
        {profile.website && (
          <a href={profile.website} target="_blank" rel="noopener noreferrer">
            <i class="fas fa-globe fa-2x"></i>
          </a>
        )}

        {
            profile.social && profile.social[0].twitte && (
                <a href={profile.social[0].twitter} target="_blank" rel="noopener noreferrer">
                <i class="fab fa-twitter fa-2x"></i>
              </a>
            )
        }

      
        <a href="#" target="_blank" rel="noopener noreferrer">
          <i class="fab fa-facebook fa-2x"></i>
        </a>
        <a href="#" target="_blank" rel="noopener noreferrer">
          <i class="fab fa-linkedin fa-2x"></i>
        </a>
        <a href="#" target="_blank" rel="noopener noreferrer">
          <i class="fab fa-youtube fa-2x"></i>
        </a>
        <a href="#" target="_blank" rel="noopener noreferrer">
          <i class="fab fa-instagram fa-2x"></i>
        </a>
      </div>
    </div>
  );
};

export default ProfileTop;
