import { Fragment } from "react";
import Moment from "react-moment";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

const CommentItem = () => {
  const dispatch = useDispatch();
  const post = useSelector((state) => state.post.post);
  const comments = useSelector((state) => state.post.post.comments);

  const auth = useSelector((state) => state.auth);
  const commentItems = comments.map((comment) => {
    return (
      <div key={comment._id} class="post bg-white p-1 my-1">
        <div>
          <Link to={`/profile/${comment.user}`}>
            <img class="round-img" src={comment.avatar} alt="" />
            <h4>{comment.name}</h4>
          </Link>
        </div>
        <div>
          <p class="my-1">{comment.text}</p>
          <p class="post-date">
            Posted <Moment format="YYYY/MM/DD">{comment.date}</Moment>
          </p>
        </div>
      </div>
    );
  });
  return <Fragment>{commentItems}</Fragment>;
};

export default CommentItem;
