import React from "react";
import Moment from "react-moment";
import { useSelector } from "react-redux";

const ProfileAbout = () => {
  const profile = useSelector((state) => state.profile.profile);

  const experience = useSelector((state) => state.profile.profile.experience);

  const experiences = experience.map((exp) => {
    return (
        <div>
            <h3 className="text-dark">{experience.company}</h3>
            <p><Moment format="YYYY/MM/DD">{experience.from}</Moment> - {!to ? "Now" : <Moment format="YYYY/MM/DD">{experience.to}</Moment>}</p>
            <p><strong>Position: </strong> {experience.title}</p>
            <p><strong>Description: </strong> {experience.description}</p>
        </div>
    );
  });
};

export default ProfileAbout;
