import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

const ProfileItem = () => {

    
    const profiles = useSelector(state => state.profile.profiles);
    
    console.log(profile);

    
    const profileItems = profiles.map((profile)=>{
        return (
            <Fragment>   <img src={profile.user.avatar} alt="" className="round-img" />
            <div>
                <h2>{profile.name}</h2>
                <p>{profile.status} {profile.company && <span> at {profile.company}</span>}</p>
                <p className="my-1">{profile.location && <span>{profile.location}</span>}</p>
                <Link to={`/profile/${profile.user._id}`} className="btn btn-primary">
                    View Profile
                </Link>
            </div>
            <ul>
                {profile.skills.slice(0,4).map((skill,index) => (<li key={index} className="text-primary">
                    <i className="fas fa-check"></i> {skill}
                </li>))}
            </ul></Fragment>
            
        )
    })
    return (
        <div className="profile bg-light">
            {profileItems}
        </div>
    )

}



export default ProfileItem
